import { useParams } from "react-router-dom";
import { useEffect, useState } from "react";
import axios from "axios"

const Details = () =>{
    
    const params = useParams();
    //console.log(params);
    const [result, setResult] = useState([]);
    http://localhost:8000/api/pirate/:id
    
    useEffect(()=>{
        axios.get(`http://localhost:8000/api/details/${params.id}/`)
             .then( response => setResult(response.data.result) )
             .catch( err => console.log(err));
    },[params.id])
    
    /*
    const remove = () =>{
        axios.delete(`http://localhost:8000/api/pets/delete/${params.id}/`)
        .then( response => setResult(response.data.result) )
        .then(alert("Congratulatios. You got a pet"))
        .then(navigate("/"))
        .catch( err => console.log(err));
    }*/

    return (
        <div>
            <div>
                <h2>Deep Sea Davy</h2>
            </div>
            <div>
                <div>
                    <img src={result.imgUrl} alt="pirate.img"/>
                </div>
                <div>
                    <h3>About</h3>
                    <p>Position: {result.crewPosition}</p>
                    <p>Treasures: {result.numOfTreasures}</p>
                    <p>Peg Leg:</p>
                    {
                        result.pegLeg ? <button>YES</button>:<button>NO</button>
                    }
                </div>
            </div>
        </div>
    )
}
export default Details
