import { useParams } from "react-router-dom";
import { useEffect, useState } from "react";
import axios from "axios"

const Details = () =>{
    
    const params = useParams();
    const [result, setResult] = useState({});
    const [hookHand, setHookHand] = useState("");
        
    
    useEffect(()=>{
        axios.get(`http://localhost:8000/api/pirate/${params.id}/`)
            //.then(response=>console.log(response))
             .then( response => setResult(response.data.result) )
             .catch( err => console.log(err));
    },[params.id])
    
    /*
    const remove = () =>{
        axios.delete(`http://localhost:8000/api/pets/delete/${params.id}/`)
        .then( response => setResult(response.data.result) )
        .then(alert("Congratulatios. You got a pet"))
        .then(navigate("/"))
        .catch( err => console.log(err));
    }*/
    
    
    /*
    const handleEye = () => {
        setEyePatch(result.eyePatch);
        if(eyePatch){
            setEyePatch(false)
            
        }else{
            setEyePatch(true)
            
        }
        setResult({
            ...result,
            eyePatch: eyePatch
        })
        editPirate(result)
    }
    */
    const handleHook = () => {
        setHookHand(result.hookHand);
        if(hookHand){
            setHookHand(false)
            
        }else{
            setHookHand(true)
            
        }
        setResult({
            ...result,
            hookHand: hookHand
        })
        editPirate(result)
    }
    
    const editPirate = (data) => {
        
        axios.put(`http://localhost:8000/api/pirate/${params.id}`,data)
            .then((response) => {
                //console.log(response)
            })
            .catch((error) => {
                console.log(error)
            },[params.id])
        
    }

    const handlePeg = (event) => {
        console.log(event.target.name)
        console.log(String(event.target.value))
        setResult({
            ...result,
            [event.target.name]: event.target.value
        })
        console.log(result)
        editPirate(result)
      
        
    }

    return (
        <div>
            <div>
                <h2>Deep Sea Davy</h2>
            </div>
            <div>
                <div>
                    <img src="" alt={result.imageUrl}/>
                </div>
                <div>
                    <h3>About</h3>
                    <p>Position: {result.crewPosition}</p>
                    <p>Treasures: {result.numOfTreasures}</p>
                    <p>Peg Leg: {result.pegLeg ? "yes":"no"} </p>
                    {
                        result.pegLeg ? <button name="pegLeg" value={!result.pegLeg} onClick={handlePeg}>NO</button>:<button name="pegLeg" value={!result.pegLeg} onClick={handlePeg}>YES</button>
                    }
                    <p>Eye Patch: {result.eyePatch? "yes":"no"}</p>
                    {
                        result.eyePatch ? <button >NO</button>:<button >YES</button>
                    }
                    <p>Hook Hand: {result.hookHand? "yes":"no"}</p>
                    {
                        result.hookHand ? <button onClick={handleHook}>NO</button>:<button onClick={handleHook}>YES</button>
                    }
                </div>
            </div>
        </div>
    )
}
export default Details
