import { useParams } from "react-router-dom";
import { useEffect, useState } from "react";
import axios from "axios"

const Details = () =>{
    
    const params = useParams();
    //console.log(params);
    const [result, setResult] = useState([]);
    //console.log(result.pegLeg)
    //const [pegLeg, setPegLeg] = useState();
    //const [hookHand, setHookHand] = useState(result.hookHand);
    //const [eyePatch, setEyePatch] = useState();
    //console.log(pegLeg)
    
    
    useEffect(()=>{
        axios.get(`http://localhost:8000/api/pirate/${params.id}/`)
            //.then(response=>console.log(response))
             .then( response => setResult(response.data.result) )
             .catch( err => console.log(err));
    },[params.id])
    
    /*
    const remove = () =>{
        axios.delete(`http://localhost:8000/api/pets/delete/${params.id}/`)
        .then( response => setResult(response.data.result) )
        .then(alert("Congratulatios. You got a pet"))
        .then(navigate("/"))
        .catch( err => console.log(err));
    }*/
    
    const handlePeg = () => {
        
        let peg= !result.pegLeg;
        
        //console.log(result)
        setResult({
            ...result,
            pegLeg: peg
        })
        console.log(result)
        
        editPirate(result)
        //console.log(result)
    }
    
    const handleEye = () => {
        let eye= !result.eyePatch;
        
        setResult({
            ...result,
            eyePatch: eye
        })
        editPirate(result)
    }
    
    const handleHook = () => {
        let hook= !result.hookHand;
        
        setResult({
            ...result,
            hookHand: hook
        })
        editPirate(result)
    }
    
    const editPirate = (data) => {
        
        axios.put(`http://localhost:8000/api/pirate/${params.id}`,data)
            .then((response) => { 
                //console.log(response)  
            })
            .catch((error) => {
                console.log(error)
            },[params.id])
        
    }


    return (
        <div>
            <div>
                <h2>Deep Sea Davy</h2>
            </div>
            <div>
                <div>
                    <img src="" alt={result.imageUrl}/>
                </div>
                <div>
                    <h3>About</h3>
                    <p>Position: {result.crewPosition}</p>
                    <p>Treasures: {result.numOfTreasures}</p>
                    <p>Peg Leg: {result.pegLeg ? "yes":"no"} </p>
                    {
                        result.pegLeg ? <button onClick={handlePeg}>NO</button>:<button onClick={handlePeg}>YES</button>
                    }
                    <p>Eye Patch: {result.eyePatch? "yes":"no"}</p>
                    {
                        <button onClick={handleEye}>{result.eyePatch? "No":"Yes"}</button>
                    }
                    <p>Hook Hand: {result.hookHand? "yes":"no"}</p>
                    {
                        <button onClick={handleHook}>{result.hookHand? "No":"Yes"}</button>
                    }
                </div>
            </div>
        </div>
    )
}
export default Details
